var assert = require('assert'),
  ochardet = require('../'),
  chardet = require('../bemuse-chardet'),
  fs = require('fs');

describe('bemuse-chardet', function() {

  var path = __dirname + '/bms';

  describe('#detectFileSync', function() {
    it('should detect encoding', function() {
      var buffer = fs.readFileSync(path + '/Altale_A.bms')
      assert.equal(chardet.detect(buffer), 'Shift-JIS'); // original: windows-1252
    });
  });
});
